public:

static bool testTable(const MultichannelMap& multimap, int* expectedIndices)
{
    for (int i = 0; i < 2048; i++)
    {
        auto mappedNote = multimap.getMappedNote(i);
        if (mappedNote.index != expectedIndices[i])
            return false;
    }
    return true;
}

