        // Check mapped notes
        for (int ch = 1; ch <= 16; ch++)
        {
            for (int note = 0; note < 128; note++)
            {
                int midiNoteIndex = (ch - 1) * 128 + note;
                int expectedIndex = expectedTable[midiNoteIndex];

                int expectedTableNum = expectedIndex / 128;
                int expectedNoteNum = expectedIndex % 128;

                auto mapped = periodMapping.getMappedNote(ch, note);
                auto detail = " @ [" + juce::String(midiNoteIndex) + "]";
                expect_exact(ch, mapped.inputChannel, "mapped.inputChannel" + detail);
                expect_exact(note, mapped.inputNote, "mapped.inputNote" + detail);
                expect_exact(expectedTableNum, mapped.tableIndex, "mapped.tableIndex" + detail);
                expect_exact(expectedNoteNum, mapped.noteIndex, "mapped.noteIndex" + detail);
                expect_exact(expectedIndex, mapped.index, "mapped.index" + detail);
            }
        }

        // Check unmapped
        auto unmapped = periodMapping.getMappedNote(0, 10000);
        expect(unmapped.mapped == false, "Input was mapped when it should be unmapped");

